import React, { useState } from 'react'
import { useParams } from 'react-router-dom'
import { ProductItem } from '../models/ProductModels'

interface UpdateProductType {
  item: ProductItem
}

const UpdateProductSection: React.FC<UpdateProductType> = () => {

  const [updateProduct, setUpdateProduct] = useState<boolean>(false)
  const [updatefailed, setUpdateFailed] = useState<boolean>(false)

  const handleDeleteProduct = async (_id: string) => {
    setUpdateProduct(false)
    setUpdateFailed(false)

    const result = await fetch(`http://localhost:5000/api/products/update`, {
      method: 'put',
      headers: {
        'Content-type': 'application/json',
        'authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: JSON.stringify([])
    })

    if (result.status === 200) {
      setUpdateProduct(true)
  } else {
      setUpdateProduct(false)
      setUpdateFailed(true)
  }

  }




  return (
    <section className='deleteProduct'>
    <div className='container'>

    {updateProduct ? (<FormNotification notificationType='success' title='Product deleted' text=''/>) : (<></>)}
    {deletefailed ? (<FormNotification notificationType='danger' title='Something went wrong!' text="We couln't delete the product - please try again later!"/>) : (<></>)}


      <form onSubmit={handleDeleteProduct}>
        <div>
          <img src={item.imageName} />
        </div>
        <div>
          <h3>{item.name}</h3>
          <p>Article number: {item.articleNumber}</p>
          <p>{item.tag}</p>
          <p>{item.description}</p>
          <p>{currencyFormatter(item.price)}</p>
          <p>{item.rating}</p>
        </div>
      <button type='submit'>DELETE PRODUCT</button>
      </form>
    </div>
    </section>
  )
}

export default UpdateProductSection
